CREATE TABLE IF NOT EXISTS Posts (Id          int NOT NULL AUTO_INCREMENT, Title       varchar(255) NOT NULL, Photo       varchar(255), Description varchar(255), Price decimal(15,2) NOT NULL, PostDate date NOT NULL, UpVotes     int NOT NULL, DownVotes   int NOT NULL, Owner       varchar(255) NOT NULL, CategoryId  int NOT NULL, PRIMARY KEY (Id),CONSTRAINT FKPosts653961 FOREIGN KEY (CategoryId) REFERENCES Categories (Id),CONSTRAINT FKPosts943508 FOREIGN KEY (Owner) REFERENCES Users (Id));
